package Assignment;

public class A17_DisplayCharacter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=65;i<=90;i++) {
    	System.out.println((char)i);
    	
    }
    System.out.println("--------------------printing smaller letter-------------------------------");
    for(char c='a';c<='z';c++) {
    	System.out.println(c);
    }

	}

}
