package typeConversion;

public class ImlicitConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 7;
		long y = x;
		double z = y;
		System.out.println("before conversion " + x);
		System.out.println("After conversion x to long " + y);
		System.out.println("After conversion x to double " + z);

	}
}
